// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "./ConvertLib.sol";

contract BossCoin {
  address public minter;
  mapping (address => uint) balances;

  event Transfer(address indexed _from, address indexed _to, uint256 _value);

  constructor() {
    minter = msg.sender;
    balances[minter] = 10000000;
  }

	function getBalance(address addr) public view returns(uint) {
		return balances[addr];
	}

  function sendCoin(address reciever, uint amount) public returns(bool sufficient) {
    if(balances[msg.sender] < amount) return false;
    balances[msg.sender] -= amount;
    balances[reciever] += amount;
    emit Transfer(msg.sender, reciever, amount);
    return true;
  }

  // function finalBossWebRegistration(address reciever, uint amount) public returns(bool sufficient) {

  // }

  function mintCoins(address reciever, uint amount) public returns(uint) {
    require(msg.sender == minter);
    balances[reciever] += amount;
    return balances[reciever];
  }
}
